import React, { useState } from 'react';

import './CSS/Chatbox.css';

const Chatbox = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  }

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  }

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'user', text: input };

    setMessages((prevMessages) => [...prevMessages, userMessage]);

    try {
      // Make an HTTP POST request to the backend API to send the user's input
      const response = await fetch('http://localhost:5001/chat', {
        method: 'POST', // Use POST method to send data to the server
        headers: {
          'Content-Type': 'application/json', // Indicate that the request body contains JSON
        },
        body: JSON.stringify({ message: input }), // Convert the message input to JSON format
      });

      const data = await response.json();
      const gptMessage = { sender: 'gpt', text: data.response };
      setMessages((prevMessages) => [...prevMessages, gptMessage]);
    } catch (error) {
      console.error('Error communicating with the backend:', error);
    } finally {
      setInput('');
    }
  };  

  return (
    <div className="chatbox">
      <div className="messages">
        {messages.map((message, index) => (
          <div key={index} className={message.sender === 'user' ? 'user-message' : 'gpt-message'}>
            {message.text}
          </div>
        ))}
      </div>  
      <div className="input-container">
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          placeholder="Type your message..."
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}

export default Chatbox;
