import React, { useState, useRef, useEffect } from 'react';

import './CSS/Chatbox.css';

const Chatbox = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const textareaRef = useRef(null);

  const [test, rawSetTest] = useState('');
  const setTest = (text) => {
    rawSetTest((prevTest) => prevTest + '\n' + text);
  }

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto'; // Reset height
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`; // Adjust height
    }
  }, [input]);

  const handleInputChange = (e) => {
    const textarea = e.target;
    setInput(e.target.value);
  }

  const handleKeyDown = (e) => {
    if (e.shiftKey && e.key === 'Enter') {
      e.preventDefault();
      setInput((prevInput) => prevInput + '\n');
    } else if (e.key === 'Enter') {
      rawSetTest('testing handleSend');
      e.preventDefault();
      handleSend();
    }
  }

  const handleSend = async () => {
    if (!input.trim()) return;

    //add new message to list
    const userMessage = { sender: 'user', text: input };
    setMessages((prevMessages) => [...prevMessages, userMessage]);

    //clear input container and reset height
    setInput('');
    //if (inputContainerRef.current) {inputContainerRef.current.style.height = 'auto'}

    try {
      const response = await fetch('http://localhost:5001/chat/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: input }),
      });

      const data = await response.json();

      if (response.ok) {
        setTest('Message received successfully')
        const gptMessage = { sender: 'gpt', text: data.response };
        setMessages((prevMessages) => [...prevMessages, gptMessage]);
      } else {
        setTest('Error: ' + data.error);
      }
    } catch (error) {
      setTest('Error: ' + error);
    }
  };

  return (
    <div className="chatbox">
      <div className="messages">
        {messages.map((message, index) => (
          <div key={index} className={message.sender === 'user' ? 'user-message' : 'gpt-message'}>
            <pre>{message.text}</pre>
          </div>
        ))}
      </div>  
      <div className="input-container">
        <textarea
          ref={textareaRef}
          type="text"
          value={input}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          placeholder="Type your message..."
        />
        <button onClick={handleSend}>Send</button>
      </div>
      <div className="development">
        <pre>{test}</pre>
      </div>
    </div>
  );
}

export default Chatbox;
